import React from 'react';

export default class Toggler extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            isTrue: false
        }
    }

    render(){
        return (
            <React.Fragment>
                <button>Toggle : {this.state.isTrue && 'T'}</button>
            </React.Fragment>
        );
    }
}
