import React from 'react';

export default class Toggler extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            isTrue: false,
            style : {
                backgroundColor: 'tomato'
            }
        }
    }

    activeToggle = (e) => {
        e.preventDefault();
        this.setState({ 
            isTrue: !this.state.isTrue,
            style: {
                backgroundColor: this.state.isTrue ? 'cornflowerblue' : 'tomato'
            }
        });
    }

    render(){
        return (
            <React.Fragment>
                <button onClick={this.activeToggle} style={this.style}>Toggle : {this.state.isTrue ? 'T' : 'F'}</button>
            </React.Fragment>
        );
    }
}
