import React from 'react';

export default class Toggler extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            isTrue: false
        }
    }

    activeToggle = (e) => {
        e.preventDefault();
        this.setState( { isTrue : !this.state.isTrue } );
    }

    render(){
        return (
            <React.Fragment>
                <button onClick={this.activeToggle}>Toggle : {this.state.isTrue && 'T'}</button>
            </React.Fragment>
        );
    }
}
